INSERT INTO `tiku` VALUES ('6', '1', '2', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal align=left style=\'text-align:left\'&gt;&lt;span style=\'font-size:\r\n11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;集合&lt;span\r\nlang=EN-US style=\'position:relative;top:8.0pt;mso-text-raise:-8.0pt\'&gt;&lt;img width=200 height=28\r\nsrc=&quot;/Public/tikupics/20151103/08/42/5638030a2ceab1446511370.gif&quot; v:shapes=&quot;_x0000_i1025&quot;&gt;&lt;/span&gt;的元素个数是（&lt;span\r\nlang=EN-US&gt;&lt;span style=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;）．&lt;span\r\nlang=EN-US&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p class=MsoNormal align=left style=\'text-align:left\'&gt;&lt;span lang=EN-US\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;A&lt;/span&gt;&lt;span\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;．&lt;span\r\nlang=EN-US&gt;59&lt;span\r\nstyle=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&lt;/span&gt;B&lt;/span&gt;．&lt;span lang=EN-US&gt;31&lt;span\r\nstyle=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&lt;/span&gt;C&lt;/span&gt;．&lt;span lang=EN-US&gt;30&lt;span\r\nstyle=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&lt;/span&gt;D&lt;/span&gt;．&lt;span lang=EN-US&gt;29&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n\r\n\r\n&lt;/div&gt;\r\n\r\n', null, '6', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal align=left style=\'text-align:left\'&gt;&lt;span lang=EN-US\r\nstyle=\'font-size:11.0pt;mso-bidi-font-s