INSERT INTO `tiku` VALUES ('16', '1', '2', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal align=left style=\'text-align:left\'&gt;&lt;span style=\'font-size:\r\n11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;如果&lt;span\r\nlang=EN-US&gt;A=&lt;span style=\'position:relative;top:5.0pt;mso-text-raise:-5.0pt\'&gt;&lt;img width=75 height=21\r\nsrc=&quot;/Public/tikupics/20151103/08/43/5638031f81ee81446511391.gif&quot; v:shapes=&quot;Picture_x0020_9&quot;&gt;&lt;/span&gt;&lt;/span&gt;，那么（&lt;span\r\nlang=EN-US&gt;&lt;span style=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;）&lt;span\r\nlang=EN-US&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p class=MsoNormal align=left style=\'text-align:left\'&gt;&lt;span lang=EN-US\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;A&lt;/span&gt;&lt;span\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;．&lt;span\r\nlang=EN-US style=\'position:relative;top:4.0pt;mso-text-raise:-4.0pt\'&gt;&lt;img width=43 height=20\r\nsrc=&quot;/Public/tikupics/20151103/08/43/5638031f9a58e1446511391.gif&quot; v:shapes=&quot;Picture_x0020_10&quot;&gt;&lt;/span&gt;&lt;span\r\nlang=EN-US&gt;&lt;span\r\nstyle=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;B&lt;/span&gt;．&lt;span\r\nlang=EN-US style=\'position:relative;top:5.0pt;mso-text-raise:-5.0pt\'&gt;&lt;img width=51 height=21\r\nsrc=&quot;/Public/tikupics/20151103/08/43/5638031fb1c931446511391.gif&quot; v:shapes=&quot;Picture_x0020_11&quot;&gt;&lt;/span&gt;&lt;span\r\nlang=EN-US&gt;&lt;span style=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&lt;/span&gt;C&lt;/span&gt;．&lt;span lang=EN-US style=\'position:relative;top:2.0pt;mso-text-raise:\r\n-2.0pt\'&gt;&lt;img width=44 height=17\r\nsrc=&quot;/Public/tiku