INSERT INTO `tiku` VALUES ('17', '1', '3', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal style=\'layout-grid-mode:char;mso-layout-grid-align:none\'&gt;&lt;span\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:10.5pt;font-family:宋体;mso-bidi-font-family:\r\nArial;color:black\'&gt;由&lt;span lang=EN-US&gt;5&lt;/span&gt;个元素构成的集合&lt;/span&gt;&lt;span lang=EN-US\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:10.5pt;font-family:宋体;color:black;\r\nposition:relative;top:7.0pt;mso-text-raise:-7.0pt\'&gt;&lt;img width=116 height=27\r\nsrc=&quot;/Public/tikupics/20151103/08/43/563803206cb441446511392.gif&quot; alt=&quot;金太阳新课标资源网(  http://wx.jtyjy.com/)&quot;\r\nv:shapes=&quot;_x0000_i1025&quot;&gt;&lt;/span&gt;&lt;span style=\'font-size:11.0pt;\r\nmso-bidi-font-size:10.5pt;font-family:宋体;color:black\'&gt;，记&lt;span lang=EN-US\r\nstyle=\'position:relative;top:2.0pt;mso-text-raise:-2.0pt\'&gt;&lt;img width=21 height=17\r\nsrc=&quot;/Public/tikupics/20151103/08/43/5638032083e621446511392.gif&quot; alt=&quot;金太阳新课标资源网(  http://wx.jtyjy.com/)&quot;\r\nv:shapes=&quot;_x0000_i1026&quot;&gt;&lt;/span&gt;的所有非空子集为&lt;span lang=EN-US\r\nstyle=\'position:relative;top:6.0pt;mso-text-raise:-6.0pt\'&gt;&lt;img width=103 height=24\r\nsrc=&quot;/Public/tikupics/20151103/08/43/563803209ad971446511392.gif&quot; alt=&quot;金太阳新课标资源网(  http://wx.jtyjy.com/)&quot;\r\nv:shapes=&quot;_x0000_i1027&quot;&gt;&lt;/span&gt;，每一个&lt;span lang=EN-US style=\'position:\r\nrelative;top:6.0pt;mso-text-raise:-6.0pt\'&gt;&lt;img width=116 height=24\r\nsrc=&quot;/Public/tikupics/20151103/08/43/56380320b343d1446511392.gif&quot; alt=&quot;金太阳新课标资源网(  http://wx.jtyjy.com/)&quot;\r\nv:shapes=&quot;_x0000_i1028&quot;&gt;&lt;/span&gt;中所有元素的积为&lt;span lang=EN-US\r\nstyle=\'position:relative;top:6.0pt;mso-text-raise:-6.0pt\'&gt;&lt;img width=19 height=24\r\nsrc=&quot;/Public/tikupics/20151103/08/43/56380320cb6fa1446511392.gif&quot; alt=&quot;金太阳新课标资源网(  http://wx.jtyjy.com/)&quot;\r\nv:shapes=&quot;_x0000_i1029&quot;&gt;&lt;/span&gt;&lt;span lang=EN-US&gt;(&lt;/span&gt;若集合中只有一个元素时，规定其积等于元素自身&lt;span\r\nlang=EN-US&gt;)&lt;/span&gt;，则&lt;span lang=EN-US style=\'position:relative;top:6.0pt;\r\nmso-text-raise:-6.0pt\'&gt;&lt;img width=193 height=24\r\nsrc=&quot;/Public/tikupics/20151103/08/43/56380320e6c811446511392.gif&quot; alt=&quot;金太阳新课标资源网(  http://wx.jtyjy.com/)&quot;\r\nv:shapes=&quot;_x0000_i1030&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;span lang=EN-US\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;mso-hansi-font-family:宋体;\r\ncolor:black\'&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n\r\n\r\n&lt;/div&gt;\r\n\r\n', null, '16', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal style=\'layout-grid-mode:char;mso-layout-grid-align:none\'&gt;&lt;span\r\nlang=EN-US style=\'font-size:11.0pt;mso-bidi-font-size:10.5pt;font-family:宋体;\r\ncolor:black;position:relative;top:2.0pt;mso-text-raise:-2.0pt\'&gt;&lt;img width=20 height=17\r\nsrc=&quot;http://i.jtyhjy.com/hstsnew/stsdoc/2/02/quehtml/20150420/answer16041175.files/image002.gif&quot; alt=&quot;金太阳新课标资源网(  http://wx.jtyjy.com/)&quot;\r\nv:shapes=&quot;_x0000_i1025&quot;&gt;&lt;/span&gt;&lt;span lang=EN-US style=\'font-size:\r\n11.0pt;mso-bidi-font-size:12.0pt;mso-hansi-font-family:宋体;color:black\'&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n\r\n\r\n&lt;/div&gt;\r\n\r\n', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal style=\'layout-grid-mode:char;mso-layout-grid-align:none\'&gt;&lt;span\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;mso-ascii-font-family:\r\n&quot;Times New Roman&quot;;color:black\'&gt;该题的设计&lt;/span&gt;&lt;span style=\'font-size:11.0pt;\r\nmso-bidi-font-size:10.5pt;font-family:宋体;mso-bidi-font-family:Arial;color:black\'&gt;是从不同的层面考查学生的思维能力，实质上，设&lt;/span&gt;&lt;span\r\nlang=EN-US style=\'font-size:11.0pt;mso-bidi-font-size:10.5pt;font