INSERT INTO `tiku` VALUES ('38', '1', '3', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal align=left style=\'text-align:left\'&gt;&lt;span style=\'font-size:\r\n11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;若集合&lt;span\r\nlang=EN-US style=\'position:relative;top:5.0pt;mso-text-raise:-5.0pt\'&gt;&lt;img width=228 height=24\r\nsrc=&quot;/Public/tikupics/20151103/08/43/5638033f75ed31446511423.gif&quot; v:shapes=&quot;_x0000_i1025&quot;&gt;&lt;/span&gt;中的元素个数为&lt;span\r\nlang=EN-US&gt;4&lt;/span&gt;，则实数&lt;span lang=EN-US style=\'position:relative;top:3.0pt;\r\nmso-text-raise:-3.0pt\'&gt;&lt;img width=15 height=19\r\nsrc=&quot;/Public/tikupics/20151103/08/43/5638033f908a21446511423.gif&quot; v:shapes=&quot;_x0000_i1026&quot;&gt;&lt;/span&gt;的取值范围是&lt;u&gt;&lt;span\r\nlang=EN-US&gt;&lt;span\r\nstyle=\'mso-spacerun:yes\'&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/u&gt;。&lt;span lang=EN-US&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n\r\n\r\n&lt;/div&gt;\r\n\r\n', null, '34', '\r\n\r\n&lt;div class=Section1 style=\'layout-grid:15.6pt\'&gt;\r\n\r\n&lt;p class=MsoNormal align=left style=\'text-align:left\'&gt;&lt;span lang=EN-US\r\nstyle=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black;\r\nposition:relative;top:12.0pt;mso-text-raise:-12.0pt\'&gt;&lt;img width=43 height=41\r\nsrc=&quot;http://i.jtyhjy.com/hstsnew/stsdoc/2/02/quehtml/20141228/answer15760608.files/image002.gif&quot; v:shapes=&quot;_x0000_i1025&quot;&gt;&lt;/span&gt;&lt;span\r\nlang=EN-US style=\'font-size:11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;\r\nmso-bidi-font-family:宋体;color:black\'&gt;.&lt;/span&gt;&lt;span lang=EN-US style=\'font-size:\r\n11.0pt;mso-bidi-font-size:12.0pt;font-family:宋体;color:black\'&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n\r\n\r\n&lt;/div&gt;\r\n\r\n', '\r\n\r